-- Create date: <2015-09-27>
-- Description:	<Get Alternative User>
-- =============================================
CREATE FUNCTION [dbo].[GetAlternativeUser] (@userName NVARCHAR(150))
RETURNS NVARCHAR(150)
AS
BEGIN
	-- Declare the return variable here
	DECLARE @TempVar NVARCHAR(150), @toDay DATE, @tempCurrentUser NVARCHAR(150), @count INT

	SELECT @toDay = getdate(), @TempVar = '', @tempCurrentUser = @userName, @count = 0

	WHILE @TempVar IS NOT NULL
		AND @tempCurrentUser <> @TempVar
		AND @count < 4
	BEGIN
		SELECT @TempVar = ''

		SELECT @TempVar = AlternativeUser
		FROM AlternativeUsers
		WHERE CurrentUser = @userName
			AND @toDay BETWEEN StartDate
				AND EndDate and status=1

		IF isnull(@TempVar, '') <> ''
		BEGIN
			SELECT @userName = @TempVar

			SELECT @count = @count + 1
		END
		ELSE
			SELECT @TempVar = NULL
	END

	RETURN @userName
END

