Create FUNCTION [dbo].[GetDateByLang] (
	@date AS DATETIME
	,@lang AS CHAR(2) = 'ar'
	)
RETURNS NVARCHAR(350)
AS
BEGIN
	DECLARE @DayName NVARCHAR(50)
		,@MonthName NVARCHAR(50)
		,@monthno INT = 1
		,@dayno VARCHAR(2)
		,@year VARCHAR(4)
		,@arbicMontyh NVARCHAR(200) = N'Ì‰«Ì—,›»—«Ì—,„«—”,«»—Ì·,„«ÌÊ,ÌÊ‰ÌÊ,ÌÊ·ÌÊ,«€”ÿ”,”» „»—,«ﬂ Ê»—,‰Ê›„»—,œÌ”„»—'
		,@englishMonth VARCHAR(200) = 'January,February,March,April,May,June,July,August,September,October,November,December'
		,@output NVARCHAR(500)

	SELECT @DayName = DATENAME(dw, @date)

	SELECT @monthno = MONTH(@date)
		,@dayno = convert(VARCHAR(2), @date, 103)
		,@year = YEAR(@date)

	IF @lang = 'ar'
		SELECT @DayName = CASE @DayName
				WHEN N'Saturday'
					THEN N'«·”» '
				WHEN N'Sunday'
					THEN N'«·√Õœ'
				WHEN N'Monday'
					THEN N'«·«À‰Ì‰'
				WHEN N'Tuesday'
					THEN N'«·À·«À«¡'
				WHEN N'Wednesday'
					THEN N'«·«—»⁄«¡'
				WHEN N'Thursday'
					THEN N'«·Œ„Ì”'
				WHEN N'Friday'
					THEN N'«·Ã„⁄…'
				END

	--select @MonthName=Data from dbo.Split(case when @lang='ar'then @arbicMontyh else  @englishMonth end,',') where ID=
	SELECT @MonthName = X.Data
	FROM (
		SELECT ROW_NUMBER() OVER (
				ORDER BY (
						SELECT 1
						)
				) Sno
			,Data
		FROM dbo.Split(CASE 
					WHEN @lang = 'ar'
						THEN @arbicMontyh
					ELSE @englishMonth
					END, ',')
		) X
	WHERE X.Sno = @monthno

	SELECT @output = Replace(REPLACE('<div class="ACTDate"><lable>{date}</lable><lable>{time}</lable></div>', '{date}', @DayName + ' ' + @dayno + ' ' + @MonthName + ' ' + @year), '{time}', CONVERT(nvarchar(5), @date, 8))

