CREATE FUNCTION [dbo].[GetLastAcivity] (@TaskID INT)
RETURNS INT
AS
BEGIN
	DECLARE @WorkFlowID INT,@CurrentActivity INT, @LastActivity INT, @OrderID SMALLINT
	SELECT @WorkFlowID = WorkFlowID,@CurrentActivity =ActivityID 
	FROM [dbo].[Tasks]
	WHERE TaskID = @TaskID
	----------
	SELECT @OrderID = Max(OrderID)
	FROM [dbo].[WorkFlowActivity]
	WHERE WorkFlowID = @WorkFlowID
	---------
	SELECT @LastActivity = ActivityID
	FROM [dbo].[WorkFlowActivity]
	WHERE WorkFlowID = @WorkFlowID AND OrderID = @OrderID and ActivityID<>@CurrentActivity
	RETURN @LastActivity
END



