CREATE FUNCTION [dbo].[GetSectorManager] (@username NVARCHAR(150))
RETURNS @tb TABLE (
	EmpID NVARCHAR(50),
	Email NVARCHAR(50),
	FullNameAR NVARCHAR(250),
	FullNameEN NVARCHAR(250),
	Username NVARCHAR(250)
	)
AS
BEGIN
	DECLARE @ManagerUsername NVARCHAR(150),
		@LoopLimt SMALLINT = 0,
		@SectorManager NVARCHAR(150),
		@ManagerID NVARCHAR(50),
		@EMPNO NVARCHAR(50),
		@SectorID NVARCHAR(150)
	SELECT @EMPNO = empno
	FROM tb_RegisterUsers
	WHERE username = @username
	IF @EMPNO IS NOT NULL
	BEGIN
		SELECT @SectorID = SectorID
		FROM [dbo].[GetUserInfoByUserName](@username,0)
		IF @SectorID IS NULL
			RETURN
		ELSE
		BEGIN
			SELECT @SectorManager = SECTOR_Username
			FROM [dbo].[Sectors]
			WHERE [SECTOR_ID] = cast(@SectorID AS BIGINT)
			INSERT INTO @tb (
				EmpID,
				Email,
				FullNameAR,
				FullNameEN,
				Username
				)
			SELECT empNO,
				EmpEmail,
				EmpNameAR,
				EmpNameEN,
				Username
			FROM dbo.GetUserInfoByUserName(@SectorManager, 1)
		END
		RETURN
	END
	ELSE
		RETURN
	IF EXISTS (
			SELECT 1
			FROM tb_SectorManager
			WHERE username = @username
			)
	BEGIN
		INSERT INTO @tb (
			EmpID,
			Email,
			FullNameAR,
			FullNameEN,
			Username
			)
		SELECT empNO,
			EmpEmail,
			EmpNameAR,
			EmpNameEN,
			Username
		FROM dbo.GetUserInfoByUserName(@username, 1)
		RETURN
	END
	WHILE @LoopLimt <= 4
		AND @SectorManager IS NULL
	BEGIN
		SELECT @ManagerUsername = NULL
		SELECT TOP (1) @ManagerUsername = MNGR_Username,
			@ManagerID = MANG_ID
		FROM dbo.GetUserInfoByUserName(@username, 1)
		IF (len(isnull(@ManagerUsername, '')) = 0)
		BEGIN
			SELECT TOP (1) @ManagerUsername = UseName,
				@ManagerID = Mngr_ID
			FROM Managers
			WHERE EmpNO = @EMPNO
			IF (len(isnull(@ManagerUsername, '')) = 0)
			BEGIN
				DELETE
				FROM @tb
				RETURN;
			END
		END
		SELECT @SectorManager = Username
		FROM tb_SectorManager
		WHERE Username = @ManagerUsername
		IF @SectorManager IS NOT NULL
		BEGIN
			INSERT INTO @tb (
				EmpID,
				Email,
				FullNameAR,
				FullNameEN,
				Username
				)
			SELECT empNO,
				EmpEmail,
				EmpNameAR,
				EmpNameEN,
				Username
			FROM dbo.GetUserInfoByUserName(@SectorManager, 1)
			RETURN
		END
		SET @LoopLimt = @LoopLimt + 1
		SELECT @username = @ManagerUsername,
			@EMPNO = @ManagerID
	END
	RETURN
END

