-- =============================================
CREATE FUNCTION [dbo].[GetUserInfoByUserName] (
	@username NVARCHAR(100)
	,@withAlternativeUsers BIT
	)
RETURNS @UserInfo TABLE (
	empNO NVARCHAR(50)
	,EmpNameAR NVARCHAR(150)
	,EmpNameEN NVARCHAR(150)
	,username NVARCHAR(100)
	,EmpEmail NVARCHAR(100)
	,MANG_ID NVARCHAR(50)
	,MNGR_NameAR NVARCHAR(150)
	,MNGR_NameEN NVARCHAR(150)
	,MNGR_Username NVARCHAR(100)
	,MNGR_EMail NVARCHAR(100)
	,jobName NVARCHAR(150)
	)
AS
BEGIN
	DECLARE @empNO NVARCHAR(50)
		,@MANG_ID NVARCHAR(50)
		,@EmpEmail NVARCHAR(150)
		,@MNGR_EMail NVARCHAR(150)
		,@MNGR_Username NVARCHAR(150)=''
		,@EmpNameAR NVARCHAR(200)
		,@EmpNameEN NVARCHAR(200)
		,@MNGR_NameAR NVARCHAR(200)
		,@MNGR_NameEN NVARCHAR(200)
		,@jobName NVARCHAR(150)
		,@toDay DATE
		,@AlternativeManager NVARCHAR(150)

	SELECT TOP 1 @MANG_ID = MNGR_ID
		,@empNO = EMPNO
		,@EmpEmail = EMAIL_ADDRESS
		,@EmpNameAR = EMPNAME
		,@EmpNameEN = ENG_NAME
		,@jobName = JOB_NAME
	FROM AllHREmployees
	WHERE EmpUserName = @username
	ORDER BY LastUpdate DESC

	IF isnull(@MANG_ID, 0) <> 0
		SELECT @MNGR_NameAR = EMPNAME
			,@MNGR_NameEN = ENG_NAME
			,@MNGR_Username = EmpUserName
			,@MNGR_EMail = EMAIL_ADDRESS
		FROM AllHREmployees
		WHERE EmpNO = @MANG_ID

	SELECT @toDay = getdate()

	IF @withAlternativeUsers = 1 and ISNULL( @MNGR_Username,'')<>''
	BEGIN
		SELECT @AlternativeManager = dbo.GetAlternativeUser(@MNGR_Username)

		--SELECT @AlternativeManager = AlternativeUser
		--FROM AlternativeUsers
		--WHERE CurrentUser = @MNGR_Username
		--	AND @toDay BETWEEN StartDate
		--		AND EndDate
		IF @AlternativeManager IS NOT NULL
			SELECT @MNGR_NameAR = EMPNAME
				,@MNGR_NameEN = ENG_NAME
				,@MNGR_Username = EmpUserName
				,@MNGR_EMail = EMAIL_ADDRESS
				,@MANG_ID = empno
			FROM AllHREmployees
			WHERE EmpUserName = @AlternativeManager
	END

	INSERT INTO @UserInfo (
		empNO
		,EmpNameAR
		,EmpNameEN
		,username
		,EmpEmail
		,MANG_ID
		,MNGR_NameAR
		,MNGR_NameEN
		,MNGR_Username
		,MNGR_EMail
		,jobName
		)
	VALUES (
		@empNO
		,@EmpNameAR
		,@EmpNameEN
		,@username
		,@EmpEmail
		,@MANG_ID
		,@MNGR_NameAR
		,@MNGR_NameEN
		,@MNGR_Username
		,@MNGR_EMail
		,@jobName
		)

	IF (
			SELECT count(1)
			FROM @UserInfo
			WHERE EmpEmail IS NULL
				OR empNO IS NULL
			) > 1
	BEGIN
		DELETE
		FROM @UserInfo

		INSERT INTO @UserInfo (
			empNO
			,EmpNameAR
			,EmpNameEN
			,username
			,EmpEmail
			,MANG_ID
			,MNGR_NameAR
			,MNGR_NameEN
			,MNGR_Username
			,MNGR_EMail
			,jobName
			)
		SELECT isnull(EMPID, EMPNO)
			,FULLNAME
			,FULLNAME
			,USERNAME
			,EMAIL
			,MANAGEREMPID
			,MANAGERNAME
			,MANAGERNAME
			,MANAGERUSERNAME
			,MANAGEREMAIL
			,JOBTITLE
		FROM ADUsers
		WHERE USERNAME = @username
	END

	RETURN
END
