CREATE FUNCTION [dbo].[PrepareParameter] (
	@TaskID INT
	,@Para NVARCHAR(Max)
	)
RETURNS NVARCHAR(Max)
AS
BEGIN
	DECLARE @ResultVar NVARCHAR(Max)
		,@FunName NVARCHAR(100)
		,@UrlApi NVARCHAR(500)
	DECLARE @Requester NVARCHAR(50)
		,@CurrentUser NVARCHAR(50)
		,@AssinTask NVARCHAR(50)
	DECLARE @CurrenUserttable AS dbo.ActiveDirectoryUsersTable
		,@RequesterUserTableas AS dbo.ActiveDirectoryUsersTable
		,@AssignTaskUserTable AS dbo.ActiveDirectoryUsersTable
	DECLARE @ActivityCurrentUsers TABLE (
		[TaskID] INT
		,[ActivityID] INT
		,GroupID INT
		,[CurrentUser] NVARCHAR(150)
		,[EMPID] NVARCHAR(50)
		,Email NVARCHAR(150)
		,GroupNameAR NVARCHAR(150)
		,GroupNameEN NVARCHAR(150)
		)
	DECLARE @DirectorOfCurrentUserTable AS dbo.ActiveDirectoryUsersTable
		,@DirectorOfRequesterUserTable AS dbo.ActiveDirectoryUsersTable
		,@DirectorOfAssignTaskUserTable AS dbo.ActiveDirectoryUsersTable
	DECLARE @TaskNo NVARCHAR(50)
		,@Title NVARCHAR(250)
		,@RefID INT
		,@TaskNote NVARCHAR(500)
		,@TaskStatusID SMALLINT
		,@WFStatusID SMALLINT
		,@TaskUrl NVARCHAR(300)
		,@TaskActionUrl NVARCHAR(300)
		,@TaskStatus_Ar NVARCHAR(50)
		,@TaskStatus_En NVARCHAR(50)
		,@WFStatus_Ar NVARCHAR(50)
		,@WFStatus_En NVARCHAR(50)
		,@GroupID INT
		,@CurrentActivityID INT
		,@Emails VARCHAR(8000)
		,@TaskDetails NVARCHAR(max)
	DECLARE @AppID INT
		,@AppName_Ar NVARCHAR(100)
		,@AppName_En NVARCHAR(100)
		,@AppViewUrl NVARCHAR(500)
		,@WorkFlowID INT
		,@WorkFlowName NVARCHAR(100)
		,@ActivityUrl NVARCHAR(500)

	-------
	SELECT @TaskActionUrl = Settings.svalue
	FROM Settings
	WHERE Settings.sid = 3

	SELECT @TaskUrl = Settings.svalue
	FROM Settings
	WHERE Settings.sid = 4

	SELECT @TaskNo = TaskNo
		,@Title = Title
		,@RefID = Tasks.RefID
		,@TaskNote = Tasks.TaskNote
		,@TaskStatusID = TaskStatus
		,@TaskDetails = TaskDetails
		,@TaskStatus_Ar = TaskStatus.STName_Ar
		,@TaskStatus_En = TaskStatus.STName_En
		,@WFStatusID = Tasks.WFStatus
		,@WFStatus_Ar = WorkFlowStatus.STName_Ar
		,@WFStatus_En = WorkFlowStatus.STName_En
		,@AppID = Tasks.AppID
		,@AppName_Ar = AppNme_Ar
		,@AppName_En = AppNme_En
		,@AppViewUrl = AppViewURL
		,@WorkFlowID = Tasks.WorkFlowID
		,@WorkFlowName = WorkFlows.WorkFlowName
		,@CurrentUser = Tasks.CurrentUser
		,@Requester = Requester
		,@AssinTask = AssignTask
		,@GroupID = Tasks.GroupID
		,@CurrentActivityID = Tasks.ActivityID
		,@ActivityUrl = isnull(WorkFlowActivity.ActivityURL, @TaskActionUrl)
	FROM Tasks
	INNER JOIN TaskStatus ON TaskStatus.STID = Tasks.TaskStatus
	INNER JOIN WorkFlowStatus ON WorkFlowStatus.STID = Tasks.WFStatus
	INNER JOIN Applications ON Tasks.AppID = Applications.AppID
	INNER JOIN WorkFlows ON WorkFlows.WFID = Tasks.WorkFlowID
	LEFT JOIN dbo.GetLastRecordOfHisoryTask(@TaskID) HT ON HT.TaskID = Tasks.TaskID
	LEFT JOIN WorkFlowActivity ON WorkFlowActivity.ActivityID = Tasks.ActivityID
	LEFT JOIN AppTasksDetails AppDet ON AppDet.TaskID = Tasks.TaskID
	WHERE Tasks.TaskID = @TaskID

	---------
	SET @Para = REPLACE(@Para, '{App:ID}', isnull(cast(@AppID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{App:ViewUrl}', isnull(@AppViewUrl, 'Null'))
	SET @Para = REPLACE(@Para, '{App:Name_En}', isnull(@AppName_En, 'Null'))
	SET @Para = REPLACE(@Para, '{App:Name_Ar}', isnull(@AppName_Ar, 'Null'))
	SET @Para = REPLACE(@Para, '{WorkFlow:ID}', isnull(cast(@WorkFlowID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{WorkFlow:Name}', isnull(@WorkFlowName, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskNo}', isnull(@TaskNo, 'Null'))
	SET @Para = REPLACE(@Para, '{Title}', isnull(@Title, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskNote}', isnull(@TaskNote, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskStatus:ID}', isnull(cast(@TaskStatusID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{WFStatus:ID}', isnull(cast(@WFStatusID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{TaskActionUrl}', isnull(@TaskActionUrl, 'Null'))
	SET @Para = REPLACE(@Para, '{ActivityUrl}', isnull(@ActivityUrl, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskUrl}', isnull(@TaskUrl, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskStatus:Name_En}', isnull(@TaskStatus_En, 'Null'))
	SET @Para = REPLACE(@Para, '{WFStatus:Name_En}', isnull(@WFStatus_En, 'Null'))
	SET @Para = REPLACE(@Para, '{TaskStatus:Name_Ar}', isnull(@TaskStatus_Ar, 'Null'))
	SET @Para = REPLACE(@Para, '{WFStatus:Name_Ar}', isnull(@WFStatus_Ar, 'Null'))
	SET @Para = REPLACE(@Para, '{RefID}', isnull(cast(@RefID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{TaskID}', isnull(cast(@TaskID AS NVARCHAR), 'Null'))
	SET @Para = REPLACE(@Para, '{App:TaskDetails}', isnull(cast(@TaskDetails AS NVARCHAR(max)), 'Null'))

	IF CHARINDEX(':username', @Para) > 0
		OR CHARINDEX(':email', @Para) > 0
		OR CHARINDEX(':fullname', @Para) > 0
		OR CHARINDEX(':empID', @Para) > 0
	BEGIN --Search Users
		IF @CurrentUser IS NOT NULL
		BEGIN
			IF CHARINDEX('CurrentUser:', @Para) > 0
			BEGIN
				IF (@GroupID IS NOT NULL)
				BEGIN
					INSERT INTO @ActivityCurrentUsers (
						TaskID
						,ActivityID
						,GroupID
						,CurrentUser
						,EMPID
						,Email
						,GroupNameAR
						,GroupNameEN
						)
					SELECT TaskID
						,ActivityID
						,GroupID
						,CurrentUser
						,ActivityCurrentUsers.EMPID
						,EMAIL
						,Grp_NameAR
						,Grp_NameEN
					FROM ActivityCurrentUsers
					INNER JOIN ADUsers ON USERNAME = CurrentUser
					INNER JOIN Groups ON Groups.Grp_ID = ActivityCurrentUsers.GroupID
					WHERE TaskID = @TaskID
						AND GroupID = @GroupID
						AND ActivityID = @CurrentActivityID

					INSERT INTO @CurrenUserttable (
						EmpID
						,Email
						,FULLNAMEAR
						,FULLNAMEEN
						,Username
						,MANAGEREMPID
						,MANAGEREMAIL
						,MANAGERNAMEAR
						,MANAGERNAMEEN
						,MANAGERUSERNAME
						)
					SELECT empNO
						,EmpEmail
						,EmpNameAR
						,EmpNameEN
						,Username
						,MANG_ID
						,MNGR_EMail
						,MNGR_NameAR
						,MNGR_NameEN
						,MNGR_Username
					FROM [dbo].GetUserInfoByUserName((
								SELECT TOP (1) CurrentUser
								FROM @ActivityCurrentUsers
								), 1)
				END
				ELSE
					INSERT INTO @CurrenUserttable (
						EmpID
						,Email
						,FULLNAMEAR
						,FULLNAMEEN
						,Username
						,MANAGEREMPID
						,MANAGEREMAIL
						,MANAGERNAMEAR
						,MANAGERNAMEEN
						,MANAGERUSERNAME
						)
					SELECT empNO
						,EmpEmail
						,EmpNameAR
						,EmpNameEN
						,Username
						,MANG_ID
						,MNGR_EMail
						,MNGR_NameAR
						,MNGR_NameEN
						,MNGR_Username
					FROM [dbo].GetUserInfoByUserName(@CurrentUser, 1)

				IF (@GroupID IS NOT NULL)
					SET @Para = REPLACE(@Para, '{CurrentUser:Username}', isnull((
									SELECT TOP (1) CurrentUser
									FROM @ActivityCurrentUsers
									), 'Null'))
				ELSE
					SET @Para = REPLACE(@Para, '{CurrentUser:Username}', isnull((
									SELECT TOP (1) username
									FROM @CurrenUserttable
									), 'Null'))

				IF (@GroupID IS NOT NULL)
				BEGIN
					SELECT @Emails = COALESCE(@Emails + ';', '') + Email
					FROM @ActivityCurrentUsers

					SET @Para = REPLACE(@Para, '{CurrentUser:Email}', isnull((@Emails), 'Null'))
				END
				ELSE
					SET @Para = REPLACE(@Para, '{CurrentUser:Email}', isnull((
									SELECT TOP (1) Email
									FROM @CurrenUserttable
									), 'Null'))

				IF (@GroupID IS NOT NULL)
					SET @Para = REPLACE(@Para, '{CurrentUser:FullName}', isnull((
									SELECT TOP (1) GroupNameAR
									FROM @ActivityCurrentUsers
									), 'Null'))
				ELSE
					SET @Para = REPLACE(@Para, '{CurrentUser:FullName}', isnull((
									SELECT TOP (1) FullNameAR
									FROM @CurrenUserttable
									), 'Null'))

				IF (@GroupID IS NOT NULL)
					SET @Para = REPLACE(@Para, '{CurrentUser:FullNameEN}', isnull((
									SELECT TOP (1) GroupNameEN
									FROM @ActivityCurrentUsers
									), 'Null'))
				ELSE
					SET @Para = REPLACE(@Para, '{CurrentUser:FullNameEN}', isnull((
									SELECT TOP (1) FullNameEN
									FROM @CurrenUserttable
									), 'Null'))

				IF (@GroupID IS NOT NULL)
					SET @Para = REPLACE(@Para, '{CurrentUser:EmpID}', isnull((
									SELECT TOP (1) EmpID
									FROM @ActivityCurrentUsers
									), 'Null'))
				ELSE
					SET @Para = REPLACE(@Para, '{CurrentUser:EmpID}', isnull((
									SELECT TOP (1) EmpID
									FROM @CurrenUserttable
									), 'Null'))

				SET @Para = REPLACE(@Para, '{ManagerOfCurrentUser:Username}', isnull((
								SELECT TOP (1) MANAGERUSERNAME
								FROM @CurrenUserttable
								), 'Null'))
				SET @Para = REPLACE(@Para, '{ManagerOfCurrentUser:Email}', isnull((
								SELECT TOP (1) MANAGEREMAIL
								FROM @CurrenUserttable
								), 'Null'))
				SET @Para = REPLACE(@Para, '{ManagerOfCurrentUser:FullName}', isnull((
								SELECT TOP (1) MANAGERNAMEAR
								FROM @CurrenUserttable
								), 'Null'))
				SET @Para = REPLACE(@Para, '{ManagerOfCurrentUser:FullNameEN}', isnull((
								SELECT TOP (1) MANAGERNAMEEN
								FROM @CurrenUserttable
								), 'Null'))
				SET @Para = REPLACE(@Para, '{ManagerOfCurrentUser:EmpID}', isnull((
								SELECT TOP (1) MANAGEREMPID
								FROM @CurrenUserttable
								), 'Null'))

				IF CHARINDEX('DirectorOfCurrentUser:', @Para) > 0
				BEGIN
					IF (@GroupID IS NOT NULL)
						SELECT TOP (1) @CurrentUser = CurrentUser
						FROM @ActivityCurrentUsers

					INSERT INTO @DirectorOfCurrentUserTable (
						EmpID
						,Email
						,FULLNAMEAR
						,FULLNAMEEN
						,Username
						)
					SELECT EmpID
						,Email
						,FullNameAR
						,FullNameEN
						,Username
					FROM dbo.GetSectorManager(@CurrentUser)

					SET @Para = REPLACE(@Para, '{DirectorOfCurrentUser:Username}', isnull((
									SELECT TOP (1) username
									FROM @DirectorOfCurrentUserTable
									), 'Null'))
					SET @Para = REPLACE(@Para, '{DirectorOfCurrentUser:Email}', isnull((
									SELECT TOP (1) Email
									FROM @DirectorOfCurrentUserTable
									), 'Null'))
					SET @Para = REPLACE(@Para, '{DirectorOfCurrentUser:FullName}', isnull((
									SELECT TOP (1) FULLNAMEAR
									FROM @DirectorOfCurrentUserTable
									), 'Null'))
					SET @Para = REPLACE(@Para, '{DirectorOfCurrentUser:FullNameEN}', isnull((
									SELECT TOP (1) FULLNAMEEN
									FROM @DirectorOfCurrentUserTable
									), 'Null'))
					SET @Para = REPLACE(@Para, '{DirectorOfCurrentUser:EmpID}', isnull((
									SELECT TOP (1) EmpID
									FROM @DirectorOfCurrentUserTable
									), 'Null'))
				END
			END
		END

		IF @Requester IS NOT NULL
		BEGIN
			IF CHARINDEX('Requester:', @Para) > 0
			BEGIN
				INSERT INTO @RequesterUserTableas (
					EmpID
					,Email
					,FULLNAMEAR
					,FULLNAMEEN
