             LEFT JOIN ADUsers ON ADUsers.EMPNO = m.EmployeeNo
        WHERE LEN(isnull(e.EmployeeNo, '')) > 0
              AND LEN(isnull(e.EmpPositionId, '')) > 0
              AND LEN(isnull(e.ManagerPositionId, '')) > 0
              AND e.EmployeeNo <> M.EmployeeNo
    ) X
