CREATE FUNCTION [dbo].[GetResponseMessage] (@Respnse NVARCHAR(max))
RETURNS TABLE
AS
RETURN (
		SELECT max(CASE 
					WHEN NAME = 'Status'
						THEN convert(NVARCHAR(max), StringValue )
					ELSE ''
					END) AS [Status], max(CASE 
					WHEN NAME = 'Response'
						THEN convert(NVARCHAR(max), StringValue )
					ELSE ''
					END) AS [Response]
		FROM dbo.parseJSON(@Respnse)
		WHERE ValueType = 'string' OR ValueType = 'boolean'
		GROUP BY parent_ID
		)



