CREATE View [dbo].[EmployeesPositionsFromERP] 
as
SELECT		DISTINCT
			
			UI.ID AS 'ERPUserID', UI.NETWORKALIAS AS 'DomainUserID1', UI.NAME 'DomainUserName1',
			CONCAT(SUBSTRING(UI.NETWORKDOMAIN,1,7),'\',UI.NETWORKALIAS) AS 'UserIDwithDomain1',
			SUI.EMAIL AS 'EmailAddress1', WORKER.PERSONNELNUMBER AS 'EmployeeID', 
			PERSON.NAME AS 'EmployeeNameEnglish', PERSON.ARABICNAME AS 'EmployeeNameArabic',
			PERSON.NATIONALITY AS 'Nationality', PERSON.QUALIFICATION AS 'Qualification', PERSON.RELIGION AS 'Religion',
			PERSON.DAPHIRINGTYPE AS 'Hiringtype', POSITION.POSITIONID AS 'PositionID',
			PDETAIL.DESCRIPTION AS 'PositionDescription', PORGIZATION.NAME AS 'Department',
			PTITLE.TITLEID AS 'PositionTitle', ETITLE.TITLEID AS 'EmployeeTitle',
			PPOSITION.POSITIONID AS 'ReportstoPositionID', PPDETAIL.DESCRIPTION AS 'ReportstoPositionDescription',
			MANAGER.PERSONNELNUMBER AS 'ReportstoEmployeeID', MGRNAME.NAME AS 'ReportstoEmployeeNameEnglish',
			MGRNAME.ARABICNAME AS 'ReportsEmployeeNameArabic', MUI.NETWORKALIAS AS 'ReportstoDomainUserID', 
			MUI.NAME 'ReportstoDomainUserName', CONCAT(SUBSTRING(MUI.NETWORKDOMAIN,1,7),'\',MUI.NETWORKALIAS) AS 'ReportstoUserIDwithDomain',
			MSUI.EMAIL AS 'ReportstoEmailAddress'


FROM MicrosoftDynamicsAX.dbo.USERINFO AS UI

INNER JOIN MicrosoftDynamicsAX.dbo.SYSUSERINFO AS SUI
ON SUI.ID = UI.ID

LEFT JOIN MicrosoftDynamicsAX.dbo.DIRPERSONUSER AS UR
ON UR.USER_ = UI.ID
AND UR.VALIDTO >= CAST(GETDATE() as date)


LEFT JOIN MicrosoftDynamicsAX.dbo.DIRPARTYTABLE AS PERSON
ON UR.PERSONPARTY = PERSON.RECID

LEFT JOIN MicrosoftDynamicsAX.dbo.HCMWORKER AS WORKER
ON WORKER.PERSON = PERSON.RECID

LEFT JOIN MicrosoftDynamicsAX.dbo.HCMEMPLOYMENT AS EMPLOYEE
ON EMPLOYEE.WORKER = WORKER.RECID
AND EMPLOYEE.VALIDTO >= CAST(GETDATE() as date)

LEFT JOIN MicrosoftDynamicsAX.dbo.HCMPOSITIONWORKERASSIGNMENT AS PWORKER
ON PWORKER.WORKER = WORKER.RECID
AND PWORKER.VALIDTO >= CAST(GETDATE() as date)


LEFT JOIN MicrosoftDynamicsAX.dbo.HCMPOSITION AS POSITION
ON PWORKER.POSITION = POSITION.RECID


LEFT JOIN MicrosoftDynamicsAX.dbo.HCMPOSITIONDETAIL AS PDETAIL
ON PWORKER.POSITION = PDETAIL.POSITION
AND PDETAIL.POSITION = POSITION.RECID
