-- Description:	Auto Close Activity 
-- =============================================
CREATE PROCEDURE [dbo].[AutoCloseActivity]
AS
BEGIN
	--IF datename(dw, getdate()) = 'Friday'
	--	OR datename(dw, getdate()) = 'Saturday'
	--	RETURN;
	DECLARE @TaskID BIGINT
		,@CurrentUser NVARCHAR(200)
		,@Response NVARCHAR(max)

	DECLARE curs CURSOR
	FOR
	SELECT TH.TaskID
		,TH.CurrentUser
	FROM TasksHistory TH
	INNER JOIN WorkFlowActivity WFA ON WFA.ActivityID = TH.ActivityID
	WHERE TH.WFStatus = 1
		AND WFA.ATypeID = 11
		AND dbo.WorkTime(TH.Created, GETDATE()) > WFA.ReminderDaysNo

	OPEN curs

	FETCH NEXT
	FROM curs
	INTO @TaskID
		,@CurrentUser

	WHILE @@FETCH_STATUS = 0
	BEGIN
		EXEC WorkFlow_DB.dbo.ApproveActivity @TaskID = @TaskID
			,@TaskNote = N' „ «€·«ﬁ «·ÿ·»  ·ﬁ«∆Ì«'
			,@AssignTask = NULL
			,@CurrentUser = @CurrentUser
			,@Result = @Response OUTPUT

		FETCH NEXT
		FROM curs
		INTO @TaskID
			,@CurrentUser
	END

	CLOSE curs

	DEALLOCATE curs
END

