CREATE PROCEDURE [dbo].[CopyWorkFlow] @WFID INT
AS
BEGIN
	DECLARE @NewWFID INT
		,@NewActivityID INT
		,@ActivityID INT
		,@ActionID INT
		,@NewActionID INT
	DECLARE @MapTable TABLE (
		CA INT
		,NA INT
		)
	DECLARE @MapTableCond TABLE (
		CA INT
		,NA INT
		)

	BEGIN TRY
		BEGIN TRANSACTION T1

		BEGIN --Copy WorkFlow Table
			INSERT INTO WorkFlows (
				[WorkFlowName]
				,[WorkFlowDescription]
				,[IsDeleted]
				,[CreationDate]
				,[MaxPosition]
				)
			SELECT [WorkFlowName]
				,[WorkFlowDescription]
				,[IsDeleted]
				,getdate()
				,[MaxPosition]
			FROM WorkFlows
			WHERE WFID = @WFID

			SELECT @NewWFID = @@IDENTITY
		END

		BEGIN --Copy Activity
			DECLARE curs_activity CURSOR
			FOR
			SELECT [ActivityID]
			FROM [dbo].[WorkFlowActivity]
			WHERE [WorkFlowID] = @WFID

			OPEN curs_activity

			FETCH NEXT
			FROM curs_activity
			INTO @ActivityID

			WHILE @@FETCH_STATUS = 0
			BEGIN --Startin Copy Activity
				INSERT INTO [dbo].[WorkFlowActivity] (
					[WorkFlowID]
					,[ReminderDaysNo]
					,[AName]
					,[ATypeID]
					,[IsDeleted]
					,[OrderID]
					,[DefaultTaskNote]
					,[CanCancel]
					,[CanReAssgin]
					,[ActivityURL]
					)
				SELECT @NewWFID
					,[ReminderDaysNo]
					,[AName]
					,[ATypeID]
					,[IsDeleted]
					,[OrderID]
					,[DefaultTaskNote]
					,[CanCancel]
					,[CanReAssgin]
					,[ActivityURL]
				FROM [dbo].[WorkFlowActivity]
				WHERE [ActivityID] = @ActivityID

				SELECT @NewActivityID = @@IDENTITY

				-- Copy ConditionActivity
				INSERT INTO ConditionActivity (
					[ActivityID]
					,[IfApprove]
					,[IfReject]
					,[IfFailed]
					,[IfCanceled]
					)
				SELECT @NewActivityID
					,[IfApprove]
					,[IfReject]
					,[IfFailed]
					,[IfCanceled]
				FROM ConditionActivity
				WHERE [ActivityID] = @ActivityID

				--Copy Actions & Emails
				DECLARE curs_actions CURSOR
				FOR
				SELECT ACID
				FROM [dbo].[Actions]
				WHERE [ActivityID] = @ActivityID

				OPEN curs_actions

				FETCH NEXT
				FROM curs_actions
				INTO @ActionID

				WHILE @@FETCH_STATUS = 0
				BEGIN --Starting Copy Actions
					INSERT INTO [dbo].[Actions] (
						[ActivityID]
						,[FunctionID]
						,[Parametars]
						,[ACName]
						,[StopIfFail]
						,[IsDeleted]
						,[OrderID]
						)
					SELECT @NewActivityID
						,[FunctionID]
						,[Parametars]
						,[ACName]
						,[StopIfFail]
						,[IsDeleted]
						,[OrderID]
					FROM [dbo].[Actions]
					WHERE [ACID] = @ActionID

					SELECT @NewActionID = @@IDENTITY

					--Starting Copy Emails
					INSERT INTO [dbo].[Emails] (
						[Recipient]
						,[Subject]
						,[Title]
						,[Body]
						,[TemplateID]
						,[ACID]
						)
					SELECT [Recipient]
						,[Subject]
						,[Title]
						,[Body]
						,[TemplateID]
						,@NewActionID
					FROM [dbo].[Emails]
					WHERE [ACID] = @ActionID

					FETCH NEXT
					FROM curs_actions
					INTO @ActionID
				END

				CLOSE curs_actions

				DEALLOCATE curs_actions

				FETCH NEXT
				FROM curs_activity
				INTO @ActivityID
			END

			CLOSE curs_activity

			DEALLOCATE curs_activity
		END

		SELECT @ActivityID = NULL
			,@NewActivityID = NULL

		INSERT INTO @MapTableCond (
			CA
			,NA
			)
		SELECT CA
			,NA
		FROM @MapTable

		DECLARE curs_MapCond CURSOR
		FOR
		SELECT CA
			,NA
		FROM @MapTable

		OPEN curs_MapCond

		FETCH NEXT
		FROM curs_MapCond
		INTO @ActivityID
			,@NewActivityID

		WHILE @@FETCH_STATUS = 0
		BEGIN
			-- Copy ConditionActivity
			INSERT INTO [ConditionActivity] (
				[ActivityID]
				,[IfApprove]
				,[IfReject]
				,[IfFailed]
				,[IfCanceled]
				)
			SELECT @NewActivityID
				,CASE 
					WHEN [IfApprove] = - 1
						OR [IfApprove] IS NULL
						THEN [IfApprove]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfApprove]
							)
					END
				,CASE 
					WHEN [IfReject] = - 1
						OR [IfReject] IS NULL
						THEN [IfReject]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfReject]
							)
					END
				,CASE 
					WHEN [IfFailed] = - 1
						OR [IfFailed] IS NULL
						THEN [IfFailed]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfFailed]
							)
					END
				,CASE 
					WHEN [IfCanceled] = - 1
						OR [IfCanceled] IS NULL
						THEN [IfCanceled]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfCanceled]
							)
					END
			FROM [ConditionActivity]
			WHERE [ActivityID] = @ActivityID

			FETCH NEXT
			FROM curs_MapCond
			INTO @ActivityID
				,@NewActivityID
		END

		CLOSE curs_MapCond

		DEALLOCATE curs_MapCond

		COMMIT TRANSACTION T1
	END TRY

	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION T1
	END CATCH
END

