		BEGIN
			-- Copy ConditionActivity
			INSERT INTO [ConditionActivity] (
				[ActivityID]
				,[IfApprove]
				,[IfReject]
				,[IfFailed]
				,[IfCanceled]
				)
			SELECT @NewActivityID
				,CASE 
					WHEN [IfApprove] = - 1
						OR [IfApprove] IS NULL
						THEN [IfApprove]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfApprove]
							)
					END
				,CASE 
					WHEN [IfReject] = - 1
						OR [IfReject] IS NULL
						THEN [IfReject]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfReject]
							)
					END
				,CASE 
					WHEN [IfFailed] = - 1
						OR [IfFailed] IS NULL
						THEN [IfFailed]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfFailed]
							)
					END
				,CASE 
					WHEN [IfCanceled] = - 1
						OR [IfCanceled] IS NULL
						THEN [IfCanceled]
					ELSE (
							SELECT NA
							FROM @MapTableCond
							WHERE CA = [IfCanceled]
							)
					END
			FROM [WorkFlow_DB_Back].[dbo].[ConditionActivity]
			WHERE [ActivityID] = @ActivityID

			FETCH NEXT
			FROM curs_MapCond
			INTO @ActivityID
				,@NewActivityID
		END

		CLOSE curs_MapCond

		DEALLOCATE curs_MapCond

		COMMIT TRANSACTION T1
	END TRY

	BEGIN CATCH
		IF @@TRANCOUNT > 0
			ROLLBACK TRANSACTION T1
	END CATCH
END

