CREATE PROCEDURE [dbo].[CreateNewTask] @AppID INT
	,@RefID INT
	,@WorkFlowID INT
	,@Title NVARCHAR(250)
	,@TitleEN NVARCHAR(250) = NULL
	,@Requester NVARCHAR(50)
	,@AssignTask NVARCHAR(150) = NULL
	,@TaskDetails NVARCHAR(max) = ''
	,@Response NVARCHAR(MAX) OUTPUT
AS
BEGIN TRY
	SET NOCOUNT ON

	DECLARE @res INT

	--EXEC @res = CheckWebAPIService @Response OUT

	--IF @res = 0
	--BEGIN
	--	RETURN - 1
	--END

	--Declaration
	DECLARE @TaskID INT
		,@TaskNo NVARCHAR(50)
		,@responsemsg NVARCHAR(MAX)

	--Insert New Task By using Parameters
	INSERT INTO [dbo].[Tasks] (
		[AppID]
		,[RefID]
		,[WorkFlowID]
		,[Title]
		,[TitleEN]
		,[Requester]
		,[CurrentUser]
		,[TaskStatus]
		,[Created]
		,[Updated]
		,[WFStatus]
		,[TaskNo]
		)
	VALUES (
		@AppID
		,@RefID
		,@WorkFlowID
		,@Title
		,@TitleEN
		,@Requester
		,''
		,1
		,GETDATE()
		,GETDATE()
		,1
		,N'000'
		)

	SET @TaskID = @@IDENTITY
	SET @TaskNo = RIGHT('00' + CAST(@AppID AS NVARCHAR), 3) + RIGHT('00' + CAST(@RefID AS NVARCHAR), 3) + RIGHT('00' + CAST(@TaskID AS NVARCHAR), 3)

	UPDATE Tasks
	SET TaskNo = @TaskNo
	WHERE TaskID = @TaskID

	IF @TaskID IS NOT NULL
		--if not null then Add To TasksHistory table and Add To Message Log table
	BEGIN
		EXEC AddToTasksHistory @TaskID = @TaskID
			,@ActivityID = NULL
			,@CurrentUser = ''
			,@WFStatus = 7
			,@TaskNote = N'Task has been Created successfully'
			,@AssignTask = @AssignTask

		EXEC AddToMessageLog @TaskID = @TaskID
			,@ActionID = NULL
			,@RequestMsg = N'Creating Task'
			,@ResponseMsg = N'Task has been Created successfully'
			,@IsOK = 1
	END --If Current Activity is null and TaskID is not null then Add To Message Log table
	ELSE IF @TaskID IS NOT NULL
		EXEC AddToMessageLog @TaskID = @TaskID
			,@ActionID = NULL
			,@RequestMsg = N'Creating Task'
			,@ResponseMsg = N'Failed to Create Task - No Activity Found Of Current Workflow'
			,@IsOK = 0

	--Get Create New Task Response
	IF @TaskID IS NOT NULL
	BEGIN
		SELECT @responsemsg = N'Task ID:' + CAST(@TaskID AS NVARCHAR) + N' has been Created successfully 
		' --+ (select top(1) msg from @t)  

		EXEC [dbo].[CreateTaskDetails] @TaskID = @TaskID
			,@AppID = @AppID
			,@RefID = @RefID
			,@ReqDetailsBody = @TaskDetails

		EXEC StartWorkFlow @TaskID
			,@Response OUTPUT

		SELECT @Response = @responsemsg + '
		' + @Response
	END
	ELSE
		SELECT @Response = N'Failed to Create Task - No Activity Found Of Current Workflow'

	RETURN isnull(@TaskID, 0)
END TRY

BEGIN CATCH
	DECLARE @ErrorMessage NVARCHAR(4000)
	DECLARE @ErrorSeverity INT
	DECLARE @ErrorState INT

	SELECT @ErrorMessage = ERROR_MESSAGE()
		,@ErrorSeverity = ERROR_SEVERITY()
		,@ErrorState = ERROR_STATE()

	IF @TaskID IS NOT NULL
	BEGIN
		UPDATE Tasks
		SET TaskStatus = 5
			,WFStatus = 8
		WHERE TaskID = @TaskID

		EXEC AddToTasksHistory @TaskID = @TaskID
			,@ActivityID = NULL
			,@CurrentUser = @Requester
			,@WFStatus = 8
			,@TaskNote = N'$CantCreateTask'
			,@AssignTask = @AssignTask

		EXEC AddToMessageLog @TaskID = @TaskID
			,@ActionID = NULL
			,@RequestMsg = N'Creating Task'
			,@ResponseMsg = N'Failed to Create Task -- Exception at CreateNewTask'
			,@IsOK = 0
	END

	SELECT @Response = @ErrorMessage

	RETURN - 1
END CATCH
