CREATE PROCEDURE [dbo].[EndWorkFlow] @TaskID INT, @TaskStatus SMALLINT, @ActionID INT = NULL, @Result NVARCHAR(max) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @ResponseMsg NVARCHAR(500), @WFS SMALLINT, @DefTaskNote NVARCHAR(250), @ActivityID INT, @HID INT

	SELECT @HID = HID, @ActivityID = ActivityID, @WFS = WFStatus
	FROM dbo.GetLastRecordOfHisoryTask(@TaskID)

	SELECT @DefTaskNote = DefaultTaskNote
	FROM WorkFlowActivity
	WHERE ActivityID = @ActionID

	SET @DefTaskNote = isnull(@DefTaskNote, N'')

	IF @TaskStatus = 3
		SELECT @ResponseMsg = N'WorkFlow of task has been completed successfully', @WFS = CASE 
				WHEN @WFS = 1
					THEN 5
				ELSE @WFS
				END
	ELSE IF @TaskStatus = 5
		SELECT @ResponseMsg = N'Workflow of task failure and End', @WFS = CASE 
				WHEN @WFS = 1
					THEN 8
				ELSE @WFS
				END
	ELSE IF @TaskStatus = 4
		SELECT @ResponseMsg = N'WorkFlow of task has been completed successfully and Closed', @WFS = CASE 
				WHEN @WFS = 1
					THEN 9
				ELSE @WFS
				END
	ELSE IF @TaskStatus = 6
		SELECT @ResponseMsg = N'WorkFlow of task has been Canceled', @WFS = 10

	UPDATE TasksHistory
	SET WFStatus = @WFS, TaskNote = CASE 
			WHEN len(isnull(TaskNote, N'')) = 0
				THEN @DefTaskNote
			ELSE TaskNote
			END, Updated = getdate()
	WHERE HID = @HID

	UPDATE Tasks
	SET TaskStatus = @TaskStatus, Updated = getdate(), WFStatus =@WFS
	WHERE TaskID = @TaskID

	EXEC AddToMessageLog @TaskID = @TaskID, @ActionID = @ActionID, @RequestMsg = N'Ending Workflow', @ResponseMsg = @ResponseMsg, @IsOk = 1

	EXEC AddToTasksHistory @TaskID = @TaskID, @ActivityID = NULL, @CurrentUser = '', @WFStatus = 5, @TaskNote = N'Done', @AssignTask = NULL

	SELECT @Result = N'WorkFlow Of TaskID:' + cast(@TaskID AS NVARCHAR) + ' Ended With Status:' + (
			SELECT STName_En
			FROM TaskStatus
			WHERE STID = @TaskStatus
			) + ' and ' + @ResponseMsg

	RETURN 1
END

