CREATE PROCEDURE [dbo].[ExecuteActivity] @TaskID INT
	,@ActivityID INT
	,@Result NVARCHAR(MAX) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	IF NOT EXISTS (
			SELECT 1
			FROM Tasks
			WHERE TaskID = @TaskID
				AND TaskStatus IN (
					1
					,2
					)
			)
	BEGIN
		SELECT @Result = N'The WorkFlow of Task ID:' + cast(@TaskID AS NVARCHAR(50)) + ' already completed or Task not found.'

		RETURN - 1
	END

	DECLARE @ACID INT
		,@StopIfFail BIT
		,@Status INT
		,@GotoNextActivity BIT
		,@msg NVARCHAR(200)
		,@currentuser NVARCHAR(100)
		,@IfFailed INT
		,@ifApproved INT
		,@DefaultTaskNote NVARCHAR(250)
		,@retvlue INT
		,@GroupID INT
		,@ActivityCurrentUser INT
		,@ATypeID INT

	IF @ActivityID IS NULL
		OR @ActivityID = 0
	BEGIN
		EXEC [dbo].[EndWorkFlow] @TaskID = @TaskID
			,@TaskStatus = 3
			,@Result = @Result OUTPUT

		RETURN 1;
	END

	SET @GotoNextActivity = 1

	SELECT @DefaultTaskNote = DefaultTaskNote
		,@ATypeID = ATypeID
	FROM WorkFlowActivity
	WHERE ActivityID = @ActivityID

	IF @ATypeID = 7
	BEGIN
		SELECT TOP (1) @ACID = [ACID]
		FROM [dbo].[Actions](NOLOCK)
		WHERE ActivityID = @ActivityID
			AND IsDeleted = 0
		ORDER BY OrderID

		IF @ACID IS NULL
		BEGIN
			SELECT @Result = N'No Action found for Conditonal ActivityID ' + CAST(@ActivityID AS NVARCHAR(50)) + '
	' + @Result

			RETURN - 1;
		END

		EXEC @Status = [ExecuteAction] @TaskID = @TaskID
			,@ActionID = @ACID
			,@Result = @Result OUTPUT

		UPDATE Tasks
		SET ActivityID = @ActivityID
		--,WFStatus = 1
		WHERE TaskID = @TaskID

		RETURN @Status
	END
	ELSE
	BEGIN
		SET @DefaultTaskNote = isnull(@DefaultTaskNote, N'')

		SELECT @IfFailed = IfFailed
			,@ifApproved = IfApprove
		FROM ConditionActivity
		WHERE ActivityID = @ActivityID

		DECLARE Curs_Activity CURSOR LOCAL FORWARD_ONLY FAST_FORWARD READ_ONLY
		FOR
		SELECT [ACID]
			,[StopIfFail]
		FROM [dbo].[Actions](NOLOCK)
		WHERE ActivityID = @ActivityID
			AND IsDeleted = 0
		ORDER BY OrderID

		OPEN Curs_Activity

		FETCH NEXT
		FROM Curs_Activity
		INTO @ACID
			,@StopIfFail

		IF @ACID IS NULL
		BEGIN
			EXEC EndWorkFlow @TaskID
				,5
				,@Result = @Result OUTPUT

			SELECT @Result = N'No Action found for ActivityID ' + CAST(@ActivityID AS NVARCHAR(50)) + '
		' + @Result

			RETURN - 1;
		END

		WHILE @@FETCH_STATUS = 0
		BEGIN
			EXEC @Status = [ExecuteAction] @TaskID = @TaskID
				,@ActionID = @ACID
				,@Result = @Result OUTPUT

			--EXEC @Status  = [ExcuteFunction] @ActionID = @ACID, @TaskID = @TaskID ,@Result=@Results OUTPUT
			IF @StopIfFail = 1
				AND @Status <= 0
			BEGIN
				SET @GotoNextActivity = 0

				BREAK
			END

			FETCH NEXT
			FROM Curs_Activity
			INTO @ACID
				,@StopIfFail
		END

		CLOSE Curs_Activity

		DEALLOCATE Curs_Activity

		IF @GotoNextActivity = 1
		BEGIN
			SELECT @currentuser = currentuser
				,@GroupID = GroupID
				,@ActivityCurrentUser = ActivityID
			FROM Tasks(NOLOCK)
			WHERE TaskID = @TaskID

			declare @WFStatus smallint
			SELECT @WFStatus = WFStatus
	FROM GetLastRecordOfHisoryTask(@TaskID)

			EXEC AddToTasksHistory @TaskID = @TaskID
				,@ActivityID = @ActivityID
				,@CurrentUser = @currentuser
				,@WFStatus = 1
				,@TaskNote = @DefaultTaskNote
				,@AssignTask = NULL

			UPDATE Tasks
			SET ActivityID = @ActivityID
				,WFStatus = 1
			WHERE TaskID = @TaskID

			UPDATE ActivityCurrentUsers
			SET ActivityID = @ActivityID
			WHERE TaskID = @TaskID
				AND GroupID = @GroupID
				AND ActivityID = @ActivityCurrentUser

			IF @ATypeID <> 9
				EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID
					,@TaskID
			ELSE
			BEGIN
				EXEC UpdateTasksHistory @TaskID = @TaskID
					,@WFStatus = @WFStatus
					,@TaskNote = N' ‰›Ì– «·ÿ·»'
					,@AssignTask = NULL

				EXEC AddToMessageLog @TaskID = @TaskID
					,@ActionID = NULL
					,@RequestMsg = N'WorkFlow Approved'
					,@ResponseMsg = 'Auto Process Activity has been executed successfully'
					,@IsOK = 1

				UPDATE Tasks
				SET TaskStatus = 1
				WHERE TaskID = @TaskID

				IF @ActivityID IS NOT NULL
					AND @ActivityID <> 0
					IF @IfApproved IS NULL
						EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID
							,@TaskID
					ELSE IF @IfApproved = - 1
						SELECT @ActivityID = NULL
					ELSE
						SELECT @ActivityID = @IfApproved

				SELECT @ActivityID = isnull(@ActivityID, 0)

				EXEC @ActivityID = ExecuteConditionActivity @ActivityID
					,@TaskID

				EXEC @Status = ExecuteActivity @TaskID = @TaskID
					,@ActivityID = @ActivityID
					,@Result = @Result OUTPUT

				RETURN @Status;
			END
		END
		ELSE
		BEGIN
			SELECT @Result = N'ActivityID ' + CAST(@ActivityID AS NVARCHAR(50)) + N' has been Executed Successfully'

			IF @IfFailed IS NULL
			BEGIN
				SELECT @currentuser = currentuser
					,@GroupID = GroupID
					,@ActivityCurrentUser = ActivityID
				FROM Tasks(NOLOCK)
				WHERE TaskID = @TaskID

				IF isnull(@currentuser, '') = ''
					AND @GroupID IS NULL
				BEGIN
					EXEC AddToTasksHistory @TaskID = @TaskID
						,@ActivityID = @ActivityID
						,@CurrentUser = @currentuser
						,@WFStatus = 8
						,@TaskNote = N'·„ Ì” ÿ⁄ «·‰Ÿ«„ „‰ ≈”‰«œ «·„Â„…'
						,@AssignTask = NULL

					UPDATE Tasks
					SET ActivityID = @ActivityID
						,WFStatus = 8
						,TaskNote = N'·„ Ì” ÿ⁄ «·‰Ÿ«„ „‰ ≈”‰«œ «·„Â„…'
					WHERE TaskID = @TaskID
				END
				ELSE
				BEGIN
					EXEC AddToTasksHistory @TaskID = @TaskID
						,@ActivityID = @ActivityID
						,@CurrentUser = @currentuser
						,@WFStatus = 8
						,@TaskNote = @DefaultTaskNote
						,@AssignTask = NULL

					UPDATE Tasks
					SET ActivityID = @ActivityID
						,WFStatus = 8
					WHERE TaskID = @TaskID
				END

				EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID
					,@TaskID

				EXEC @retvlue = ExecuteActivity @TaskID
					,@ActivityID
					,@Result OUTPUT

				RETURN @retvlue;
			END
			ELSE IF @IfFailed = - 1
			BEGIN
				EXEC EndWorkFlow @TaskID
					,5
					,@Result = @Result OUTPUT

				RETURN - 1;
			END
			ELSE
			BEGIN
				SELECT @currentuser = currentuser
					,@GroupID = GroupID
					,@ActivityCurrentUser = ActivityID
				FROM Tasks(NOLOCK)
				WHERE TaskID = @TaskID

				IF isnull(@currentuser, '') = ''
					AND @GroupID IS NULL
				BEGIN
					EXEC AddToTasksHistory @TaskID = @TaskID
						,@ActivityID = @ActivityID
						,@CurrentUser = @currentuser
						,@WFStatus = 8
						,@TaskNote = N'·„ Ì” ÿ⁄ «·‰Ÿ«„ „‰ ≈”‰«œ «·„Â„…'
						,@AssignTask = NULL

					UPDATE Tasks
					SET ActivityID = @ActivityID
						,WFStatus = 8
						,TaskNote = N'·„ Ì” ÿ⁄ «·‰Ÿ«„ „‰ ≈”‰«œ «·„Â„…'
					WHERE TaskID = @TaskID
				END
				ELSE
				BEGIN
					EXEC AddToTasksHistory @TaskID = @TaskID
						,@ActivityID = @ActivityID
						,@CurrentUser = @currentuser
						,@WFStatus = 8
						,@TaskNote = @DefaultTaskNote
						,@AssignTask = NULL

					UPDATE Tasks
					SET ActivityID = @ActivityID
						,WFStatus = 8
					WHERE TaskID = @TaskID
				END

				SELECT @ActivityID = @IfFailed

				EXEC @retvlue = ExecuteActivity @TaskID
					,@ActivityID
					,@Result OUTPUT

				RETURN @retvlue;
			END
		END

		IF @ActivityID IS NULL
			OR @ActivityID = 0
		BEGIN
			EXEC [dbo].[EndWorkFlow] @TaskID = @TaskID
				,@TaskStatus = 3
				,@Result = @Result OUTPUT

			RETURN 1;
		END

		SELECT @Result = N'ActivityID ' + CAST(@ActivityID AS NVARCHAR(50)) + N' has been Executed Successfully'

		RETURN 1;
	END
END

