CREATE PROCEDURE [dbo].[ExecuteConditionActivity] @ActivityID INT,
	@TaskID INT
AS
BEGIN
	DECLARE @WorkFlowID INT,
		@OrderID SMALLINT,
		@Atype SMALLINT,
		@IfApprove INT,
		@IfReject INT,
		@IfFailed INT,
		@Status INT,
		@result NVARCHAR(max)
	IF isnull(@ActivityID, 0) = 0
		RETURN 0;
	SELECT @Atype = ATypeID
	FROM [dbo].[WorkFlowActivity]
	WHERE ActivityID = @ActivityID
	WHILE @Atype = 7
	BEGIN
		SELECT @IfApprove = IfApprove,
			@IfReject = IfReject,
			@IfFailed = IfFailed
		FROM ConditionActivity
		WHERE ActivityID = @ActivityID
		EXEC @Status = ExecuteActivity @TaskID,
			@ActivityID,
			@result OUTPUT
		IF @Status = 1
		BEGIN
			IF @IfApprove IS NULL
				EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID,
					@TaskID
			ELSE IF @IfApprove = - 1
				SELECT @ActivityID = NULL
			ELSE
				SELECT @ActivityID = @IfApprove
		END
		ELSE IF @Status = 0
		BEGIN
			IF @IfReject IS NULL
				EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID,
					@TaskID
			ELSE IF @IfReject = - 1
				SELECT @ActivityID = NULL
			ELSE
				SELECT @ActivityID = @IfReject
		END
		ELSE
		BEGIN
			IF @IfFailed IS NULL
				EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID,
					@TaskID
			ELSE IF @IfFailed = - 1
				SELECT @ActivityID = NULL
			ELSE
				SELECT @ActivityID = @IfFailed
		END
		IF isnull(@ActivityID, 0) = 0
			RETURN 0;
		SET @Atype = NULL
		SELECT @Atype = ATypeID
		FROM [dbo].[WorkFlowActivity]
		WHERE ActivityID = @ActivityID
	END --
	Return @ActivityID;
END

