CREATE PROCEDURE [dbo].[GetADSearch] @attribute NVARCHAR(100), @searchword NVARCHAR(200), @flag NVARCHAR(20) = N'Any', @AccountStatus nvarchar(50) = N'null'
AS
BEGIN
	
	IF @attribute NOT IN (N'EmpID', N'Email', N'DisplayName', N'FullName', N'Username', N'JobTitle',N'MANAGERUSERNAME',N'MANAGEREMAIL',N'MANAGEREMPID',N'MANAGERNAME',N'Branch')
		SET @attribute = N'FullName'
	
	DECLARE @sql NVARCHAR(max) = 'SELECT isnull(EMPID, '''') EmpID
		, Email
		,FullName FullNameEN
		, FullName FullNameAR
		, Username
		, MANAGERUSERNAME
		, MANAGEREMAIL
		, MANAGEREMPID
		, MANAGERNAME MANAGERNAMEEN
		, MANAGERNAME MANAGERNAMEAR
		, Branch
		, JobTitle
		, Extention
	FROM ADUsers where #p0 like ''#p1'''
	
	IF isnull(@searchword, '') = ''
	BEGIN
	SELECT isnull(EMPID, '') EmpID
		, Email
		, DisplayName
		, FullName
		, Username
		, MANAGERUSERNAME
		, MANAGEREMAIL
		, MANAGEREMPID
		, MANAGERNAME
		, Branch
		, JobTitle
		, Extention
	FROM ADUsers where 1<>1
		RETURN;
	END
	IF @flag = N'Exact'
		SELECT @sql = REPLACE(replace(@sql, N'#p0', @attribute), N'#p1', @searchword)
	ELSE IF @flag = N'First'
		SELECT @sql = REPLACE(replace(@sql, N'#p0', @attribute), N'#p1', @searchword + N'%')
	ELSE IF @flag = N'Any'
		SELECT @sql = REPLACE(replace(@sql, N'#p0', @attribute), N'#p1', N'%' + @searchword + N'%')
	ELSE IF @flag = N'Last'
		SELECT @sql = REPLACE(replace(@sql, N'#p0', @attribute), N'#p1', N'%' + @searchword)
	ELSE
		SELECT @sql = REPLACE(replace(@sql, N'#p0', @attribute), N'#p1', @searchword + N'%')
	IF isnull(@AccountStatus,'null')=N'null' or isnull(@AccountStatus,N'')=N''
		SELECT @sql = @sql + ' And  len(isnull(Email,''''))>0'
	ELSE IF @AccountStatus = N'1'
		SELECT @sql = @sql + ' And ISENABLED = 1 and len(isnull(Email,''''))>0'
	ELSE
		SELECT @sql = @sql + ' And ISENABLED =0'
	PRINT @sql
	EXEC (@sql)
END


