		,Created -- RequestDate
		,CASE 
			WHEN isnull(app.AppViewURL, '?') = '?'
				THEN Replace(@MyRequestURL, '{TaskID}', Cast(TaskID AS NVARCHAR(10)))
			ELSE Replace(Replace(app.AppViewURL, '{TaskID}', Cast(TaskID AS NVARCHAR(10))), '{RefID}', cast(RefID AS NVARCHAR(10)))
			END RequestUrl
	FROM Tasks
	INNER JOIN Applications App ON Tasks.AppID = App.AppID
	--INNER JOIN ADUsers ReqUser ON ReqUser.USERNAME = Requester
	INNER JOIN TaskStatus ON Tasks.TaskStatus = TaskStatus.STID
	WHERE Requester = @Username -- and Tasks.TaskStatus=2 
		AND (
			@TaskStatus IS NULL
			OR Tasks.TaskStatus = @TaskStatus
			)
	--ORDER BY Tasks.Updated DESC, Tasks.TaskStatus
	ORDER BY Created DESC
