CREATE PROCEDURE [dbo].[GetMyAllTasks] (
	@Username NVARCHAR(150)
	,@TaskStatus SMALLINT
	)
AS
BEGIN
	DECLARE @MyTaskURL NVARCHAR(250)
		,@TaskCount INT

	SELECT @MyTaskURL = svalue
	FROM Settings
	WHERE [sid] = 3

	SELECT DISTINCT Tasks.TaskID
		,TaskNo
		,Title
		,ISNULL(TitleEN, app.AppNme_En) TitleEN
		, ReqUser.empname FullName
		,AppNme_Ar
		,AppNme_En
		,Tasks.Created --CreatedDate
		,CASE 
			WHEN TH.WFStatus IN (
					1
					,4
					,6
					)
				THEN Replace(Replace(isnull(WorkFlowActivity.ActivityURL, @MyTaskURL), '{TaskID}', Cast(Tasks.TaskID AS NVARCHAR(10))), '{RefID}', Cast(Tasks.RefID AS NVARCHAR(10)))
			ELSE '#'
			END TaskUrl
		,WS.STName_Ar TaskStatus_AR
		,WS.STName_En TaskStatus_EN
	FROM Tasks
	INNER JOIN TasksHistory TH ON TH.TaskID = Tasks.TaskID
	INNER JOIN Applications App ON Tasks.AppID = App.AppID
	LEFT JOIN AllHREmployees ReqUser ON ReqUser.EmpUserName = Requester
	--LEFT JOIN tb_RegisterUsers CUser ON CUser.username = TH.CurrentUser
	LEFT JOIN WorkFlowActivity ON WorkFlowActivity.ActivityID = Tasks.ActivityID
	INNER JOIN WorkFlowStatus WS ON TH.WFStatus = WS.STID
	WHERE (
			TH.CurrentUser = @Username
			OR (
				--CUser.username IS NULL
				--AND 
				TH.GroupID IS NOT NULL
				AND @Username IN (
					SELECT currentuser
					FROM ActivityCurrentUsers ACU
					WHERE ACU.TaskID = Tasks.TaskID
						AND ACU.GroupID = Tasks.GroupID
						AND ACU.ActivityID = Tasks.ActivityID
					)
				)
			)
		AND (
			@TaskStatus IS NULL
			OR TH.WFStatus = @TaskStatus
			)
		AND TH.ActivityID IS NOT NULL
	--and Requester <>TH.CurrentUser
	ORDER BY tasks.Created DESC
END

