	WHERE [sid] = 3

	SELECT TOP (3) TaskNo
		,Title
		,ISNULL(TitleEN, app.AppNme_En) TitleEN
		,CASE 
			WHEN isnull(ReqUser.deflang,'AR') = 'AR'
				THEN ReqUser.empname
			ELSE ReqUser.empnameen
			END FullName
		,AppNme_Ar
		,AppNme_En
		,Tasks.Created CreatedDate
		,Replace(Replace(isnull(WorkFlowActivity.ActivityURL, @MyTaskURL), '{TaskID}', Cast(TaskID AS NVARCHAR(10))), '{RefID}', Cast(RefID AS NVARCHAR(10))) TaskUrl
	FROM Tasks
	INNER JOIN Applications App ON Tasks.AppID = App.AppID
	INNER JOIN tb_RegisterUsers ReqUser ON ReqUser.username = Requester
	LEFT JOIN tb_RegisterUsers CUser ON CUser.username = CurrentUser
	LEFT JOIN WorkFlowActivity ON WorkFlowActivity.ActivityID = Tasks.ActivityID
	WHERE (
			CurrentUser = @Username
			OR (
				CUser.username IS NULL
				AND GroupID IS NOT NULL
				AND @Username IN (
					SELECT currentuser
					FROM ActivityCurrentUsers ACU
					WHERE ACU.TaskID = Tasks.TaskID
						AND ACU.GroupID = Tasks.GroupID
						AND ACU.ActivityID = Tasks.ActivityID
					)
				)
			)
		AND Tasks.WFStatus IN (
			1
			,4
			,6
			)
		AND Tasks.ActivityID IS NOT NULL
	ORDER BY CreatedDate
END

