					WHEN len(rtrim(ltrim(isnull([TaskNote], '')))) = 0
						--THEN N'·« ÌÊÃœ „·«ÕŸ« '
						THEN N'$NoCommentsMsg'
					ELSE [TaskNote]
					END
			END TaskNote
		,dbo.GetDateByLang(TH.[Created], @deflang) Created
		--,TH.[Created]-- TH.[Created]
		,[Updated]
		,WFS.STName_Ar WFStatus_ar
		,WFS.STName_En WFStatus_en
		,CASE 
			WHEN AName IS NULL
				AND TH.ActivityID IS NULL
				AND WFStatus <> 7
				THEN N'$RequestCompletedMsg'
			WHEN AName IS NULL
				THEN N'$InitiateRequestMsg'
			ELSE [AName]
			END AName
		,TH.WFStatus
		,WFA.ATypeID
	FROM [dbo].[TasksHistory] TH
	INNER JOIN WorkFlowStatus WFS ON [WFStatus] = WFS.STID
	LEFT JOIN [dbo].[WorkFlowActivity] WFA ON WFA.ActivityID = TH.ActivityID
	LEFT JOIN ADUsers ON ADUsers.USERNAME = [CurrentUser]
	LEFT JOIN Groups ON TH.GroupID = Grp_ID
	LEFT JOIN AllHREmployees ON AllHREmployees.empusername = [CurrentUser]
	--LEFT JOIN Managers ON Managers.UseName = [CurrentUser]
	--LEFT JOIN tb_RegisterUsers ON tb_RegisterUsers.username = [CurrentUser]
	WHERE (TH.TaskID = @TaskID)
		AND (
			WFA.ATypeID <> 5
			OR WFA.ActivityID IS NULL
			)
		AND TH.WFStatus <> 5
		AND ISNULL(WFA.ShowInHistory, 1) <> 0
		AND (
			WFA.ATypeID <> 9
			OR (
				WFA.ATypeID = 9
				AND TH.WFStatus = 8
				)
			OR WFA.ActivityID IS NULL
			)
END
