CREATE PROCEDURE [dbo].[InsertTempEmployeeAndGetData] @EMPNO NVARCHAR(50)
	,@ANNUAL_BALANCE NVARCHAR(50)
	,@EMERGENCY_BALANCE NVARCHAR(50)
AS
BEGIN
	IF EXISTS (
			SELECT 1
			FROM [dbo].[TEMPEMPLOYEBALANCE]
			WHERE EMPNO = @EMPNO
			)
		UPDATE [dbo].[TEMPEMPLOYEBALANCE]
		SET [ANNUAL_BALANCE] = @ANNUAL_BALANCE
			,[EMERGENCY_BALANCE] = @EMERGENCY_BALANCE
			,[EXPIARY_DATE] = DATEADD(HOUR, 3, getdate())
		WHERE EMPNO = @EMPNO
	ELSE
		INSERT INTO [dbo].[TEMPEMPLOYEBALANCE] (
			[EMPNO]
			,[ANNUAL_BALANCE]
			,[EMERGENCY_BALANCE]
			,[EXPIARY_DATE]
			)
		VALUES (
			@EMPNO
			,@ANNUAL_BALANCE
			,@EMERGENCY_BALANCE
			,DATEADD(HOUR, 3, getdate())
			)

	SELECT EMPBLNC.[EMPNO]
		,EMPBLNC.[ANNUAL_BALANCE]
		,EMPBLNC.[EMERGENCY_BALANCE]
		,isnull(HR.EMPNAME, RU.empname) FullNameAr
		,RU.empnameen FullNameEn
		,isnull(HR.SECT_ID, RU.sectorid) SECT_ID
		,isnull(Sectors.SECTOR_NAME, isnull(HR.SECT_NAME, RU.empsector)) SECT_NAME
		,Sectors.SECTOR_NAME_EN as SECT_NAMEEN
		,Sectors.SECTOR_URL SECT_URL
		,isnull(HR.ORG_ID, RU.orgid) ORG_ID
		,isnull(HR.ORG_NAME, RU.emporg) ORG_NAME
		,isnull(HR.REGION_ID, RU.branchid) REGION_ID
		,isnull(HR.REGION_NAME, RU.empbranch) REGION_NAME
	FROM [dbo].[TEMPEMPLOYEBALANCE] EMPBLNC
	LEFT JOIN AllHREmployees HR ON HR.[EMPNO] = EMPBLNC.[EMPNO]
	LEFT JOIN tb_RegisterUsers RU ON RU.empno = EMPBLNC.[EMPNO]
	LEFT JOIN Sectors ON Sectors.SECTOR_ID = isnull(HR.SECT_ID, RU.sectorid)
	WHERE (
			DATEDIFF(mi, cast(getdate() AS SMALLDATETIME), isnull([EXPIARY_DATE], cast(getdate() AS SMALLDATETIME))) > 0
			OR HR.EMPNO IS NULL
			)
		AND EMPBLNC.[EMPNO] = @EMPNO
END

