CREATE PROCEDURE [dbo].[ReassignActivity] @TaskID INT
	,@TaskNote NVARCHAR(500)
	,@CurrentUser NVARCHAR(150)
	,@Result NVARCHAR(max) OUTPUT
AS
BEGIN
	SET NOCOUNT ON

	DECLARE @res INT
		,@Notes NVARCHAR(max)

	SELECT @Notes = N'TastID is ' + cast(@TaskID AS NVARCHAR) + N' - Current User is: ' + @CurrentUser + N' - Function is: ReassignActivity'

	EXEC @res = CheckWebAPIService @TaskID = @TaskID
		,@Notes = @Notes
		,@Response = @Result OUT

	IF @res = 0
	BEGIN
		SELECT @Result = N'ÕœÀ Œÿ√ €Ì— „ Êﬁ⁄ Ì—ÃÏ «·„Õ«Ê·… „—Â «Œ—Ï - Unknown Error - Please try again later'

		RETURN - 1
	END
	ELSE
		SELECT @Result = ''

	DECLARE @TaskStatus SMALLINT

	SELECT @TaskStatus = TaskStatus
	FROM Tasks
	WHERE TaskID = @TaskID
		AND TaskStatus IN (
			1
			,2
			)

	IF @TaskStatus IS NULL
	BEGIN
		SELECT @Result = N'The WorkFlow of Task ID:' + cast(@TaskID AS NVARCHAR(50)) + ' already completed,Approving or Task not found.'

		RETURN - 1
	END

	UPDATE Tasks
	SET TaskStatus = 7
	WHERE TaskID = @TaskID

	DECLARE @ActivityID INT
		,@Status INT
		,@IfReassign INT
		,@TaskCurrentUser NVARCHAR(150)
		,@CurrentUserGroup INT

	SELECT @ActivityID = ActivityID
		,@TaskCurrentUser = CurrentUser
		,@CurrentUserGroup = GroupID
	FROM Tasks
	WHERE TaskID = @TaskID

	IF @TaskCurrentUser <> @CurrentUser
		AND @CurrentUserGroup IS NULL
	BEGIN
		SELECT @Result = N'Access denied error: Curent User diffrent'

		UPDATE Tasks
		SET TaskStatus = @TaskStatus
		WHERE TaskID = @TaskID

		RETURN - 1
	END

	IF @CurrentUserGroup IS NOT NULL
	BEGIN
		IF NOT EXISTS (
				SELECT 1
				FROM ActivityCurrentUsers
				WHERE CurrentUser = @CurrentUser
					AND TaskID = @TaskID
					AND GroupID = @CurrentUserGroup
					AND ActivityID = @ActivityID
				)
		BEGIN
			SELECT @Result = N'Access denied error: Curent User diffrent'

			UPDATE Tasks
			SET TaskStatus = @TaskStatus
			WHERE TaskID = @TaskID

			RETURN - 1
		END
	END

	UPDATE Tasks
	SET CurrentUser = @CurrentUser
	WHERE TaskID = @TaskID

	EXEC UpdateTasksHistory @TaskID = @TaskID
		,@WFStatus = 11
		,@TaskNote = @TaskNote

	EXEC AddToMessageLog @TaskID = @TaskID
		,@ActionID = NULL
		,@RequestMsg = N'WorkFlow Re-Assign'
		,@ResponseMsg = @TaskNote
		,@IsOK = 1

	UPDATE Tasks
	SET TaskStatus = @TaskStatus
	WHERE TaskID = @TaskID

	IF @ActivityID IS NOT NULL
		AND @ActivityID <> 0
	BEGIN
		SELECT @IfReassign = IfReAssign
		FROM ConditionActivity
		WHERE ActivityID = @ActivityID

		IF @IfReassign IS NULL
			EXEC @ActivityID = dbo.GetNextAcivitySP @ActivityID
				,@TaskID
		ELSE IF @IfReassign = - 1
			SELECT @ActivityID = NULL
		ELSE
			SELECT @ActivityID = @IfReassign
	END

	SELECT @ActivityID = isnull(@ActivityID, 0)

	EXEC @ActivityID = ExecuteConditionActivity @ActivityID
		,@TaskID

	EXEC @Status = ExecuteActivity @TaskID = @TaskID
		,@ActivityID = @ActivityID
		,@Result = @Result OUTPUT

	RETURN @Status
END

