	INNER JOIN Tasks T ON T.TaskID = TH.TaskID
	INNER JOIN Applications APP ON APP.AppID = T.AppID
	WHERE DATEDIFF(day, ISNULL(TH.ReminderDate, TH.Created), GETDATE()) > WA.ReminderDaysNo
		AND (WA.ATypeID <> 11)
		AND TH.WFStatus IN (
			1
			,4
			,6
			)
		AND TH.GroupID IS NOT NULL
		AND T.TaskStatus IN (
			1
			,2
			)
		AND ISENABLED = 1

	OPEN cursgroup

	FETCH NEXT
	FROM cursgroup
	INTO @HID
		,@senderID
		,@SendTO
		,@subject
		,@title
		,@TemplateID
		,@body

	WHILE @@FETCH_STATUS = 0
	BEGIN
		--SELECT @HID
		--	,@senderID
		--	,@SendTO
		--	,@subject
		--	,@title
		--	,@TemplateID
		--	,@body
		
		EXEC HNotifications.dbo.MessagesAddSingleEmail @senderID
			,@SendTO
			,@subject
			,@body
			,@title
			,@TemplateID

		UPDATE TasksHistory
		SET ReminderDate = GETDATE()
		WHERE HID = @HID

		FETCH NEXT
		FROM cursgroup
		INTO @HID
			,@senderID
			,@SendTO
			,@subject
			,@title
			,@TemplateID
			,@body
	END

	CLOSE cursgroup

	DEALLOCATE cursgroup
END

