CREATE PROCEDURE [dbo].[StartWorkFlow] @TaskID INT
	,@Result NVARCHAR(Max) OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @Activity INT
		,@Response NVARCHAR(Max)
		,@wfid INT
		,@Status INT

	EXEC AddToMessageLog @TaskID = @TaskID
		,@ActionID = NULL
		,@RequestMsg = N'Starting WorkFlow'
		,@ResponseMsg = N'Initial WorkFlow'
		,@IsOk = 1

	SELECT @wfid = WorkFlowID
	FROM Tasks
	WHERE Tasks.TaskID = @TaskID

	SELECT TOP (1) @Activity = ActivityID
	FROM WorkFlowActivity
	WHERE WorkFlowID = @wfid
		AND IsDeleted = 0
		order by WorkFlowActivity.OrderID
	IF @Activity IS NULL
		OR @Activity = 0
	BEGIN
		EXEC AddToMessageLog @TaskID = @TaskID
			,@ActionID = NULL
			,@RequestMsg = N'Starting WorkFlow'
			,@ResponseMsg = N'Failed to starting workflow on task -- No Activities found'
			,@IsOk = 0

		SELECT @Result = N'Failed to starting workflow on task -- No Activities found'

		RETURN - 1;
	END

	UPDATE Tasks
	SET TaskStatus = 2
		,WFStatus = 1
		,TaskNote = N'WorkFlow Started'
	WHERE TaskID = @TaskID

	SET @Response = N'WorkFlow Of Task ID:' + cast(@TaskID AS NVARCHAR) + N' has been Started successfully'

	EXEC AddToMessageLog @TaskID = @TaskID
		,@ActionID = NULL
		,@RequestMsg = N'WorkFlow Started'
		,@ResponseMsg = @Response
		,@IsOk = 1

	EXEC @Activity = dbo.GetCurrentAcivitySP @TaskID

	EXEC @Activity = ExecuteStartActivity @TaskID, @Activity
		,@Result OUT
	IF @Activity = - 1
	BEGIN
		RETURN - 1;
	END
	EXEC @Activity = ExecuteConditionActivity @Activity
		,@TaskID
	EXEC ExecuteActivity @TaskID
		,@Activity
		,@Result = @Result OUTPUT

	SELECT @Result = @Response + N'
' + @Response

	RETURN 1;
END

