CREATE PROCEDURE [dbo].[UpdateAlternativeUsersStatus] @AUID INT,
	@WFStatus SMALLINT,
	@Response NVARCHAR(MAX) OUTPUT
AS
BEGIN
	DECLARE @AUStatus BIT,
		@AUUsername NVARCHAR(150),
		@CurrentUser NVARCHAR(150),
		@StartDate DATE,
		@EndDate DATE
	BEGIN TRY
		SELECT @AUStatus = [status],
			@AUUsername = AlternativeUser,
			@CurrentUser = CurrentUser,
			@StartDate = StartDate,
			@EndDate = EndDate
		FROM AlternativeUsers
		WHERE ID = @AUID
		IF @AUUsername IS NULL
		BEGIN
			SELECT @Response = N'No Alternative User found for RefID=' + cast(@AUID AS NVARCHAR(50))
			RETURN - 1
		END
		IF @AUStatus IS NOT NULL
		BEGIN
			SELECT @Response = N'Already Action been Taken before for Alternative User RefID=' + cast(@AUID AS NVARCHAR(50))
			RETURN - 2
		END
		IF @WFStatus = 2
		BEGIN
			BEGIN
				UPDATE AlternativeUsers
				SET [status] = 1,
					Updated = getdate()
				WHERE ID = @AUID
				INSERT INTO AlternativeUsersLog (
					[CurrentUser],
					[AlternativeUser],
					[StartDate],
					[EndDate],
					[status]
					)
				VALUES (
					@CurrentUser,
					@AUUsername,
					@StartDate,
					@EndDate,
					1
					)
			END
		END
		ELSE
		BEGIN
			UPDATE AlternativeUsers
			SET [status] = 0,
				Updated = getdate()
			WHERE ID = @AUID
			INSERT INTO AlternativeUsersLog (
				[CurrentUser],
				[AlternativeUser],
				[StartDate],
				[EndDate],
				[status]
				)
			VALUES (
				@CurrentUser,
				@AUUsername,
				@StartDate,
				@EndDate,
				0
				)
		END
		SELECT @Response = N'AU RefID:' + cast(@AUID AS NVARCHAR(50)) + N' has been ' + CASE 
				WHEN @WFStatus = 3
					THEN N'Rejected'
					When @WFStatus=10 Then N'Canceled'
				ELSE N'Approved'
				END
		RETURN @AUID
	END TRY
	BEGIN CATCH
		DECLARE @ErrorMessage NVARCHAR(4000);
		DECLARE @ErrorSeverity INT;
		DECLARE @ErrorState INT;
		SELECT @ErrorMessage = ERROR_MESSAGE(),
			@ErrorSeverity = ERROR_SEVERITY(),
			@ErrorState = ERROR_STATE();
		SELECT @Response = @ErrorMessage
		RETURN - 1
	END CATCH
END
