CREATE PROCEDURE [dbo].[UpdateTasksHistory] @TaskID INT
	,@WFStatus SMALLINT
	,@TaskNote NVARCHAR(500)
	,@AssignTask NVARCHAR(100) = NULL
AS
BEGIN
	DECLARE @HID INT
		,@CurrentUser NVARCHAR(150)
		,@groupID INT

	SELECT @CurrentUser = CurrentUser
		,@groupID = GroupID
	FROM Tasks
	WHERE TaskID = @TaskID

	SELECT @HID = HID
	FROM GetLastRecordOfHisoryTask(@TaskID)

	UPDATE [dbo].[TasksHistory]
	SET [WFStatus] = isnull(@WFStatus, WFStatus)
		,[TaskNote] = @TaskNote
		,AssignTask = @AssignTask
		,[Updated] = getdate()
		,CurrentUser = CASE 
			WHEN @groupID IS NULL
				THEN CurrentUser
			ELSE @CurrentUser
			END
	WHERE HID = @HID

	UPDATE [dbo].[Tasks]
	SET [WFStatus] = @WFStatus
		,[TaskNote] = @TaskNote
		,[TaskStatus] = 2
		,GroupID = NULL
	WHERE TaskID = @TaskID
END

